var suggestions=document.getElementById('suggestions'),userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(a){a.keyCode===191&&(a.preventDefault(),userinput.focus()),a.keyCode===27&&(userinput.blur(),suggestions.classList.add('d-none'))}document.addEventListener('click',function(a){var b=suggestions.contains(a.target);b||suggestions.classList.add('d-none')}),document.addEventListener('keydown',suggestionFocus);function suggestionFocus(b){const d=suggestions.querySelectorAll('a'),e=[...d],a=e.indexOf(document.activeElement);let c=0;b.keyCode===38?(b.preventDefault(),c=a>0?a-1:0,d[c].focus()):b.keyCode===40&&(b.preventDefault(),c=a+1<e.length?a+1:a,d[c].focus())}(function(){var b=new FlexSearch({preset:'score',cache:!0,doc:{id:'id',field:['title','description','content'],store:['href','title','description']}}),c=[{id:0,href:"/docs/gallery/photos/",title:"Gallery",description:"A image gallery of community built multiblocks",content:'\u003cp\u003e\u003cimg src="/images/czb%C3%BCendel-industrialkiln.png" alt="Industrial Kiln made by Czbüendel" title="Czbüendel - Industrial Kiln"\u003e\u003c/p\u003e\n'},{id:1,href:"/docs/structures/exmaples/",title:"Examples",description:"Example datapack recipe files for defining the shape of a machine.",content:'\u003ch1 id="the-beginner"\u003eThe Beginner\u003c/h1\u003e\n\u003cpre\u003e\u003ccode class="language-json"\u003e{\n  \u0026quot;type\u0026quot;: \u0026quot;masterfulmachinery:machine_structure\u0026quot;,\n  \u0026quot;controllerId\u0026quot;: [\u0026quot;\u0026quot;]\n}\n\u003c/code\u003e\u003c/pre\u003e\n'},{id:2,href:"/docs/config/controller/",title:"Step By Step",description:"Config for defining Controllers and Ports.",content:'\u003ch2 id="why-not-datapack-for-the-blocks"\u003eWhy not DataPack For The Blocks?\u003c/h2\u003e\n\u003cp\u003eIn minecraft blocks and items are registered a long time before the datapack\u0026rsquo;s are even loaded. This means that to register blocks and items from JSON files, the mod must have a non-datapack-driven way of creating and configuring those blocks and such.\u003c/p\u003e\n\u003ch1 id="creating-a-controller-block"\u003eCreating A Controller Block\u003c/h1\u003e\n\u003cp\u003eFirstly, we must create the block which controls and handles the machines and recipes. This block is called a Controller.\u003c/p\u003e\n\u003cp\u003eTo create a controller block you must navigate to the following directory/folder relative to the \u003ccode\u003e.minecraft\u003c/code\u003e or the equivalent root directory/folder for your modpack.\u003c/p\u003e\n\u003cp\u003ePath:  \u003ccode\u003e/config/masterful_machinery/controllers\u003c/code\u003e.\u003c/p\u003e\n\u003cp\u003eOnce you are inside the above directory. create a new JSON file, you can call it anything you want as long as it ends in \u003ccode\u003e.json\u003c/code\u003e.\u003c/p\u003e\n\u003cp\u003eOpen the JSON file in your favorite JSON supporting text editor\u003c/p\u003e\n'},{id:3,href:"/docs/intro/welcome/",title:"Welcome",description:"Welcome to the Masterful Machinery wiki.",content:'\u003ch1 id="what-is-masterful-machinery"\u003eWhat is Masterful Machinery?\u003c/h1\u003e\n\u003cp\u003eMasterful Machinery is a forge minecraft mod written with modpack developers in mind. The mod was written to allow modpack developers to quickly and easily create processing Input/Output machines with multiple blocks, through the power of JSON files and datapacks.\u003c/p\u003e\n\u003cp\u003eIt\u0026rsquo;s goal is to be enabling, whilst providing the flexibility and power to the more advanced users.\u003c/p\u003e\n\u003ch1 id="who-is-involved"\u003eWho is Involved?\u003c/h1\u003e\n\u003cblockquote\u003e\n\u003cp\u003eTicTicBoooom (Lead Mod Developer/Owner)\u003c/p\u003e\n\u003c/blockquote\u003e\n\u003cblockquote\u003e\n\u003cp\u003eRidanisaurus (Lead Artist/Designer)\u003c/p\u003e\n\u003c/blockquote\u003e\n\u003ch1 id="where-can-i-"\u003eWhere Can I \u0026hellip;?\u003c/h1\u003e\n\u003cul\u003e\n\u003cli\u003e\u003cstrong\u003eReport a bug/issue\u003c/strong\u003e:\n\u003cul\u003e\n\u003cli\u003eIf you know that the issue is 100% a new bug and not just a mistake, then report on GitHub: \u003ca href="https://github.com/TicTicBoooom-Mods/MasterfulMachinery/issues"\u003e\u003cstrong\u003eHERE\u003c/strong\u003e\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eIf you need any other type of help or support with the mod, feel free to join out helpful discord community: \u003ca href="https://discord.gg/kmeabQq5gs"\u003e\u003cstrong\u003eHERE\u003c/strong\u003e\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eGet Some Assistance/Help\u003c/strong\u003e:\n\u003cul\u003e\n\u003cli\u003eJoin our discord community full of people ready to help and support you when using and learning the mod: \u003ca href="https://discord.gg/kmeabQq5gs"\u003e\u003cstrong\u003eHERE\u003c/strong\u003e\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003c/ul\u003e\n'},{id:4,href:"/docs/config/",title:"Controller Config",description:"MM Controller Config",content:""},{id:5,href:"/docs/intro/",title:"Introduction",description:"Introduction",content:""},{id:6,href:"/docs/structures/",title:"Structures",description:"MM Structure Recipes",content:""},{id:7,href:"/docs/",title:"Docs",description:"Docs Doks.",content:""},{id:8,href:"/docs/gallery/",title:"Photo Album",description:"A gallery of community built multiblocks",content:""}];b.add(c),userinput.addEventListener('input',e,!0),suggestions.addEventListener('click',f,!0);function e(){var g=this.value,e=b.search(g,5),f=suggestions.childNodes,h=0,i=e.length,c;for(suggestions.classList.remove('d-none'),e.forEach(function(b){c=document.createElement('div'),c.innerHTML='<a href><span></span><span></span></a>',a=c.querySelector('a'),t=c.querySelector('span:first-child'),d=c.querySelector('span:nth-child(2)'),a.href=b.href,t.textContent=b.title,d.textContent=b.description,suggestions.appendChild(c)});f.length>i;)suggestions.removeChild(f[h])}function f(){while(suggestions.lastChild)suggestions.removeChild(suggestions.lastChild);return!1}})()